            "url"        : "/2020/08/17/cara-mengatasi-error-flutter-part-2/",
            "path"       : "_posts/2020-08-17-cara-mengatasi-error-flutter-part-2.md",
            "next"       : "/2020/08/21/caraku-lolos-apple-developer-acedemi-2021/",
            "previous"   : "/2020/08/16/cara-mengatasi-error-flutter-part-1/",
