            "next"       : "/2019/04/05/Membuat-Aplikasi-Foodbank-Part-2-(Menambah-Welcome-Screen)/",
            "previous"   : "/2019/03/26/membuat-aplikasi-foodbank-part-1/",
            "date"       : "2019-03-30 00:00:00 +0700",
